"use client";

import { motion } from "framer-motion";

export function PoweredBy() {
  const items = [
    { href: "https://developers.cloudflare.com/agents", name: "Cloudflare Agents", logo: <CloudflareLogo /> },
    { href: "https://baseten.co", name: "Baseten Model API", logo: <BasetenLogo /> },
    { href: "https://together.ai", name: "Together Orpheus TTS", logo: <TogetherLogo /> },
    { href: "https://vercel.com", name: "Vercel Web App", logo: <VercelLogo /> },
    { href: "https://parallel.ai", name: "Parallel Web Search", logo: <ParallelLogo /> },
  ];

  return (
    <section className="bg-background border-y border-border/50">
      <div className="mx-auto max-w-4xl px-6 py-10 lg:py-12">
        <motion.div
          className="flex flex-col items-center justify-center gap-8"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-50px" }}
          transition={{ duration: 0.6 }}
        >
          <span className="text-sm text-muted-foreground/70 uppercase tracking-widest font-semibold">
            Powered by
          </span>
          
          <div className="flex flex-wrap items-center justify-center gap-8 md:gap-12">
            {items.map((item, index) => (
              <a
                key={index}
                href={item.href}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-3 opacity-50 hover:opacity-100 transition-all duration-300 hover:scale-105 group"
              >
                <div className="h-10 w-10 dark:h-11 dark:w-11">
                  {item.logo}
                </div>
                <span className="text-lg md:text-xl font-bold text-muted-foreground group-hover:text-foreground">
                  {item.name}
                </span>
              </a>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
}

